import { useSelector } from "react-redux";
import Notification from "./components/UI/Notification";

function App() {
  const showCart = useSelector((state) => state.cart.isCartVisible);
  return (
    <>
      <Notification
        status="success"
        title="SUCCESS"
        message="sdfsf sdfsduyhfshdfjs dfsudf"
      />
      <Layout>
        {showCart && <Cart />}
        <Products />
      </Layout>
    </>
  );
}

export default App;
