import { createSlice } from "@reduxjs/toolkit";

const cartItemsSlice = createSlice({
  name: "cartItemsSlice",
  initialState: {
    items: [
      /* {id:"" ,name: "", price: "", quantity: ""} */
    ],
    totalQuantity: 0,
    totalPrice: 0,
  },
  reducers: {
    addItemToCart(state, action) {
      const itemToBeAdded = action.payload;
      const doItemExist = state.items[0];
      if (!doItemExist) {
        state.items.push(itemToBeAdded);
        state.totalQuantity = state.totalQuantity + itemToBeAdded.quantity;
        state.totalPrice = state.totalPrice + itemToBeAdded.price;
      } else {
        console.log(doItemExist);
      }
    },
    removeItemFromCart() {},
  },
});

export const cartItemsActions = cartItemsSlice.actions;

export default cartItemsSlice;
