import React, { useState } from "react";

export const AuthContext = React.createContext({
  email: "",
  token: "",
  isLoggedIn: false,
  login: (loginConfig) => {}, //loginConfig => {email: "", token: ""}
  logout: () => {},
});

function calculateTimeToLogout(timeFromFirebase) {
  const now = new Date().getTime(); // in miliseconds
  console.log(now);
}

const AuthContextProvider = (props) => {
  const getTokenFromLS = localStorage.getItem("token");

  const [token, setToken] = useState(getTokenFromLS);
  const [email, setEmail] = useState(null);

  const isLoggedIn = !!token;

  function loginHandler(loginConfig) {
    const { email, token, expiresIn } = loginConfig;
    setToken(token);
    setEmail(email);
    localStorage.setItem("token", token);
    calculateTimeToLogout(expiresIn);
  }

  function logoutHandler() {
    setToken(null);
    setEmail(null);
    localStorage.removeItem("token");
  }

  const contextValue = {
    email: email,
    token: token,
    isLoggedIn: isLoggedIn,
    login: loginHandler,
    logout: logoutHandler,
  };

  return (
    <AuthContext.Provider value={contextValue}>
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthContextProvider;
